mapper.select:
select q_num, q_writer, q_email, q_subject, q_password, q_regdate, q_readcount, q_ref, q_re_step, q_re_level, q_content, q_ip from (select rownum as rank, q_num, q_writer, q_email, q_subject, q_password, q_regdate, q_readcount, q_ref, q_re_step, q_re_level, q_content, q_ip from (select q_num, q_writer, q_email, q_subject, q_password, q_regdate, q_readcount, q_ref, q_re_step, q_re_level, q_content, q_ip from q_board
where q_writer like #{keyword}
where q_subject like #{keyword}
order by q_ref desc, q_re_step asc ))

select count(*) from q_board 

	where q_writer like #{keyword}


	where q_subject like #{keyword}

order by q_num asc

select c_num, c_writer, c_email, c_subject, c_password, c_regdate, c_readcount, c_ref, c_re_step, c_re_level, c_content, c_ip from (select rownum as rank, c_num, c_writer, c_email, c_subject, c_password, c_regdate, c_readcount, c_ref, c_re_step, c_re_level, c_content, c_ip from (select c_num, c_writer, c_email, c_subject, c_password, c_regdate, c_readcount, c_ref, c_re_step, c_re_level, c_content, c_ip from c_board
where c_writer like #{keyword}
where c_subject like #{keyword}
order by c_ref desc, c_re_step asc ))

select count(*) from c_board 

	where c_writer like #{keyword}


	where c_subject like #{keyword}

order by c_num asc

select * from users

	where (u_id like #{keyword} or
	u_name like #{keyword} or
	u_phone like #{keyword})

		 and u_jointype = 'S'


		 and u_jointype = #{filter}


		 and u_color like #{filter}


		order by u_joindate #{filter}



	where u_name like #{keyword}

		 and u_jointype = 'S'


		 and u_jointype = #{filter}


		 and u_color like #{filter}


		order by u_joindate #{filter}



	where u_id like #{keyword}

		 and u_jointype = 'S'


		 and u_jointype = #{filter}


		 and u_color like #{filter}


		order by u_joindate #{filter}



	where u_phone like #{keyword}

